const intersectionObserver=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add("shown");}else{entry.target.classList.remove("shown");}});},{threshold:0.5});const intersectionObserver2=new IntersectionObserver((entries,obs)=>{entries.forEach(entry=>{if(entry.isIntersecting){const el=entry.target;el.removeAttribute('hidden');obs.unobserve(el);}});},{threshold:0.3});const resizeObserver=new ResizeObserver(visibilityChange);function visibilityChange(){document.querySelectorAll(".md-nav__list .md-nav__item").forEach(item=>{item.style.display="";});document.querySelectorAll(".tabbed-block").forEach(block=>{const visibility=window.getComputedStyle(block);if(visibility.display==="none"){const headings=new Set(Array.from(block.querySelectorAll("h2, h3, h4, h5, h6")).map(h=>h.getAttribute("id")).filter(id=>id!==null));document.querySelectorAll(".md-nav__list .md-nav__item a.md-nav__link").forEach(a=>{const url=new URL(a.href);const frag=url.hash.slice(1);if(headings.has(frag)){const navItem=a.closest(".md-nav__item");if(navItem){navItem.style.display="none";}}});}});};document$.subscribe(function(){const header=document.querySelector(".md-header");function getHeaderHeight(){return header?header.offsetHeight:0;}
window.addEventListener("scroll",function(){if(window.scrollY>getHeaderHeight()){header.classList.add("scrolled");}else{header.classList.remove("scrolled");}});window.addEventListener("resize",function(){getHeaderHeight();});const titles=document.querySelectorAll("h1, h2, h3");titles.forEach(el=>intersectionObserver.observe(el));const animated=document.querySelectorAll('[hidden]');animated.forEach(el=>intersectionObserver2.observe(el));document.querySelectorAll(".tabbed-block").forEach(block=>{resizeObserver.observe(block);});});